	@Override
	public boolean emailValidation(User user) {
		
		boolean matcherFound = false;
		String[] mails = {"admin@chico.com","editor@chico.com","user@chico.com"};
		
		String control = user.getEmail();

		for (String mail :mails) {
			if(mail == control) {
				matcherFound = true;
			}
			
		}
		
		if(matcherFound) {
			return true;
		}else {
			return false;
		}
	}
	
	
	@Override
	public boolean validation(User user) {
		if(user.getFirstName().length() < 2) {
			System.out.println("************************************************************");
			System.out.println("Adýnýz en az 2 karakterli olmalýdýr");
			return true;
		}else if(user.getLastName().length() < 2) {
			System.out.println("************************************************************");
			System.out.println("Soyadýnýz en az 2 karakterli olmalýdýr");
			return true;
		}else if(user.getPassword().length() < 6) {
			System.out.println("************************************************************");
			System.out.println("Þifreniz en az 6 karakterli olmalýdýr");
			return true;
		}else if(this.emailValidation(user)) {
			System.out.println("************************************************************");
			System.out.println("E posta adresiniz daha önceden alýnmýþ ");
			return true;
		}else if(this.emailValidating(user)) {
			System.out.println("************************************************************");
			System.out.println("E posta adresinizi mail kurallarýna göre girin");
		}
		return false;
		
	}


	
		

